export const setEvents = events => ({
  type: 'SET_EVENTS',
  events,
})

export const setEvent = event => ({
  type: 'SET_EVENT',
  event,
})

export const setDates = dates => ({
  type: 'SET_DATES',
  dates,
})

export const setRegionFilter = regions => ({
  type: 'SET_REGION_FILTER',
  regions,
})
