import React from 'react'
import { connect } from 'react-redux'
import EventCard from './EventCard'
import stringifyClassnames from 'app/helpers/stringifyClassnames'
import SearchBar from 'app/components/SearchBar'
import EventListFilters from 'app/components/EventListFilters'

import styles from './EventList.less'

const EventList = ({ events, className }) => {
  return (
    <>
      <SearchBar
        filterContent={<EventListFilters />}
      />
      <section className={stringifyClassnames([styles.eventlist, className])}>
        {events.length == 0 ? <section>No events</section> : null}
        {events.map((event, index) => <EventCard key={index} {...event} />)}
      </section>
    </>
  )
}

const mapStateToProps = state => ({
  events: state.events
})

const mapDispatchToProps = dispatch => ({})

export default connect(mapStateToProps, mapDispatchToProps)(EventList)
