import React, { useState, useCallback, useEffect } from 'react'
import EventListFilters from 'app/components/EventListFilters'
import styles from './EventListFilterPanel.less'
import stringifyClassnames from 'app/helpers/stringifyClassnames'

const EventListFilterPanel = ({ className }) => {
  const [ visible, setVisible ] = useState(false)
  const [ btnLabel, setBtnLabel ] = useState("Show Filters")

  const handleShowFilters = (e) => {
    e.preventDefault()
    setVisible(!visible)
    setBtnLabel(!visible ? "Hide Filters" : "Show Filters")
  }

  return (
    <div className={stringifyClassnames(styles.filters, className)}>
      <input type="button" value={btnLabel} onClick={handleShowFilters} />
      {visible && <EventListFilters /> }
    </div>
  )
}

export default EventListFilterPanel
