import React, { useState, useCallback } from 'react'
import styles from './EventListFilters.less'
import stringifyClassnames from 'app/helpers/stringifyClassnames'

export default ({ className }) => {
  const [visible, setVisible] = useState(false)
  const btnOnClick = useCallback(e => {
    e.preventDefault()
    setVisible(!visible)
  })

  return (
    <form className={stringifyClassnames(styles.filters, className)}>
      <button onClick={btnOnClick}>
        Filters
      </button>
      {visible && <div>Filters visible</div>}
    </form>
  )
}
