import React from 'react'
import { connect } from 'react-redux'
import EventList from 'app/components/EventList'
import Event from 'app/components/Event'
import { Helmet } from 'react-helmet'

import styles from './EventPage.less'
import stringifyClassnames from 'app/helpers/stringifyClassnames'

const EventPage = ({ event }) => {
  return (
  <div className={stringifyClassnames([styles.eventpage, event ? styles.event : styles.noevent])}>
    <Helmet>
      <title>Events</title>
    </Helmet>
    <EventList className={styles.eventlist} />
    {event ? <Event /> : null }
  </div>
)}

const mapStateToProps = state => ({
  event: state.event
})

const mapDispatchToProps = dispatch => ({
  // setEvents: events => dispatch(setEvents(events))
})


export default connect(mapStateToProps, mapDispatchToProps)(EventPage)
