import React, { useState } from 'react'

import SearchBar from 'app/components/SearchBar'
import EventListFilters from 'app/components/EventListFilters'
import FilteredEventList from 'app/components/FilteredEventList'

import styles from './EventsPage.less'

export default () => {
  const [showFilters, setShowFilters] = useState(false)
  return (
    <div className={styles.container}>
      <SearchBar onClickFilterToggle={() => setShowFilters(!showFilters)} />
      {showFilters && <EventListFilters onSubmit={() => setShowFilters(false)}/>}
      <h1>Events</h1>
      <FilteredEventList />
    </div>
  )
}
