import React from 'react'

export default ({ title, content }) => {
  const cssClassNameFromString = s => {
    return s
      .toLowerCase()
      .replace(/[^a-z0-9]/g,'-')  // non-word to hyphen
      .replace(/--/g,'-') // remove duplicate hyphens
      .replace(/^-+/g,'') // remove leading hyphens
      .replace(/-+$/g,'') // remove trailing hyphens
    }

  if (!content) return null
  return (
    <>
      <dt className={'meta ' + cssClassNameFromString(title)}>{title}</dt>
      <dd className={'meta ' + cssClassNameFromString(title)}>{content}</dd>
    </>
  )
}
