import React, { useState } from 'react'

import styles from './styles.less'

export default ({
  filterContent,
  className = styles.container,
  searchIconClass = "far fa-search",
  filterIconClass = "far fa-sliders-h",
}) => {
  const [ showFilters, setShowFilters ] = useState(false)
  return (
    <section className={className}>
      <span className={searchIconClass}></span>
      <input type="text" placeholder="Search" />
      <a className={filterIconClass} href="javascript:void(0);" onClick={e => setShowFilters(!showFilters)}></a>
      {showFilters && filterContent}
    </section>
  )
}
