import React from 'react'

export default ({
  className,
  title,
  options = [],
  idKey = "id",
  labelKey = "description",
  selected = [],
  onChange = () => {}
}) => {
  return (
    <div className={className}>
      <h1>{title}</h1>
      {options.map(option => {
        return (
          <div key={option[idKey]}>
            <input
              type="checkbox"
              checked={selected.indexOf(option[idKey]) !== -1}
              onChange={e => onChange(option[idKey], e.target.checked)} />
            <label>{option[labelKey]}</label>
          </div>
        )
      })}
    </div>
  )
}
