import React from 'react'
import stringifyClassnames from 'app/helpers/stringifyClassnames'
import styles from './SelectedOptions.less'

export default ({
  className,
  title,
  options = [],
  idKey = "id",
  labelKey = "description",
  selected = [],
  onChange = () => {}
}) => {
  return (
    <fieldset className={stringifyClassnames(styles.options, className)}>
      <legend>{title}</legend>
      {options.map(option => {
        return (
          <label key={option[idKey]}>
            <input
              type="checkbox"
              checked={selected.indexOf(option[idKey]) !== -1}
              onChange={e => onChange(option[idKey], e.target.checked)} />
            {option[labelKey]}
          </label>
        )
      })}
    </fieldset>
  )
}
