import { combineReducers } from 'redux'

const regions = (state = [], { type, regions }) => {
    switch (type) {
      case 'SET_REGION_FILTER':
        return regions.sort()
      default:
        return state
    }
}

const disciplines = (state = [], { type, disciplines }) => {
    switch (type) {
      case 'SET_DISCIPLINE_FILTER':
        return disciplines.sort()
      default:
        return state
    }
}

export default combineReducers({ regions, disciplines })
