import { combineReducers } from 'redux'

const regions = (state = [], { type, regions }) => {
  switch (type) {
    case 'SET_REGION_FILTER':
      return regions.sort()
  }
  return state
}

const disciplines = (state = [], { type, disciplines }) => {
  switch (type) {
    case 'SET_DISCIPLINE_FILTER':
      return disciplines.sort()
  }
  return state
}

const dates = (state = [], { type, dates }) => {
  switch(type) {
    case 'SET_DATE_FILTER':
      return dates.sort()
  }
  return state
}

export default combineReducers({ regions, disciplines, dates })
