export default event => {
  if (event.venue && event.venue.tags) {
    event.venue.tags.forEach(tag => {
      const category = tag.tag_category.plural.toLowerCase()
      if (!Array.isArray(event[category])) {
        event.venue[category] = []
      }
      event.venue[category].push({ id: tag.id, description: tag.description })
    })
    delete event.venue.tags
  }
  return event
}
